import java.io.BufferedReader;import java.io.FileReader;import java.util.ArrayList;import java.util.List;public class Day12Part2 {public static void main(String[] args) throws Exception {String file = "12/input";BufferedReader br = null;br = new BufferedReader(new FileReader(file));String line;List<Instruction> insts = new ArrayList<>();while ((line = br.readLine()) != null) { insts.add(new Instruction(line)); }br.close();Ship myShip = new Ship(1, 10);myShip.doInstructions(insts);myShip.ManhattanDistance();}static class Instruction {char op;int arg;Instruction(String line) {op = line.charAt(0);arg = Integer.parseInt(line.substring(1));}}static class Ship {static class Waypoint {int n_s, e_w;Waypoint(int n_s, int e_w) {this.n_s = n_s;this.e_w = e_w;}}Waypoint waypoint;int n_s, e_w;Ship(int n_s, int e_w) {waypoint = new Waypoint(n_s, e_w);this.n_s = this.e_w = 0;}void doInstructions(List<Instruction> instList) {for (int i = 0; i < instList.size(); i++) {doInstruction(instList.get(i));}}void doInstruction(Instruction instr) {int aux = -1;if (instr.op == 'N') {waypoint.n_s += instr.arg;} else if (instr.op == 'S') {waypoint.n_s -= instr.arg;} else if (instr.op == 'E') {waypoint.e_w += instr.arg;} else if (instr.op == 'W') {waypoint.e_w -= instr.arg;} else if (instr.op == 'L') {for (int i = 0; i < instr.arg / 90; i++) {aux = waypoint.e_w;waypoint.e_w = -waypoint.n_s;waypoint.n_s = aux;}} else if (instr.op == 'R') {for (int i = 0; i < instr.arg / 90; i++) {aux = waypoint.n_s;waypoint.n_s = -waypoint.e_w;waypoint.e_w = aux;}} else if (instr.op == 'F') {n_s += instr.arg * waypoint.n_s;e_w += instr.arg * waypoint.e_w;}}void ManhattanDistance() {System.out.println(Math.abs(n_s) + Math.abs(e_w));}}}
